import React from 'react';
import OutputScreen from './outputScreen.js';
import Button from './button.js';
import '../index.css';

class NumberCruncher extends React.Component {
  constructor() {
    super();

    this.state = {
      question: '',
      answer: ''
    }
    this.handleClick = this.handleClick.bind(this);
  }
  render() {
    return (
      <div className="main">
        <h2> Number Cruncher</h2>
        <div>
          <OutputScreen question={this.state.question} answer={this.state.answer} />
          <br></br>
          <div className='row'>
            <Button className='button-style' label={'Clear'} handleClick={this.handleClick} />
            <Button className='button-style' label={'Delete'} handleClick={this.handleClick} />
            <Button className='button-style' label={'='} handleClick={this.handleClick} />
          </div>
          <div>
          <Button className='button-style' label={'8'} handleClick={this.handleClick} />
            <Button className='button-style' label={'9'} handleClick={this.handleClick} />
            <Button className='button-style' label={'.'} handleClick={this.handleClick} />
          </div>
          <div>
          <Button className='button-style' label={'6'} handleClick={this.handleClick} />
            <Button className='button-style' label={'7'} handleClick={this.handleClick} />
            <Button className='button-style' label={'*'} handleClick={this.handleClick} />
          </div>
          <div>
          <Button className='button-style' label={'4'} handleClick={this.handleClick} />
            <Button className='button-style' label={'5'} handleClick={this.handleClick} />
            <Button className='button-style' label={'+'} handleClick={this.handleClick} />
          </div>
          <div>
          <Button className='button-style' label={'2'} handleClick={this.handleClick} />
            <Button className='button-style' label={'3'} handleClick={this.handleClick} />
            <Button className='button-style' label={'/'} handleClick={this.handleClick} />
          </div>
          <div>
            <Button className='button-style' label={'0'} handleClick={this.handleClick} />
            <Button className='button-style' label={'1'} handleClick={this.handleClick} />
            <Button className='button-style' label={'-'} handleClick={this.handleClick} />
          </div>
        </div>
      </div>
    );
  }
  handleClick(event) {

    const value = event.target.value;

    switch (value) {
      case '=': {

        if (this.state.question !== '') {
          var ans = '';
          try {
            ans = eval(this.state.question);
            console.log(ans);
          }
          catch (err) {
            this.setState({ answer: "Math Error" });
          }
          if (ans === undefined)
            this.setState({ answer: "Math Error" });

          else
            this.setState({ answer: ans, question: '' });
          break;
        }
      }
      case 'Clear': {

        this.setState({ question: '', answer: '' });
        break;
      }

      case 'Delete': {
        var str = this.state.question;
        str = str.substr(0, str.length - 1);
        this.setState({ question: str });
        break;
      }

      default: {

        this.setState({ question: this.state.question += value })
        console.log(this.state);
        break;
      }
    }
  }
}
export default NumberCruncher;
